import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import PostService from "../services/post.service";
import AuthService from "../services/auth.service";
import { Link } from "react-router-dom";

const EventDetails = () => {
  const { id } = useParams();
  const [eventDetails, setEventDetails] = useState(null);
  const [loading, setLoading] = useState(true);
  const [formData, setFormData] = useState({
    name: "",
    description: "",
    place: "",
    date: "",
    time: "",
    maxParticipants: 0,
  });
  const user = AuthService.getCurrentUser();

  useEffect(() => {
    const fetchEventDetails = async () => {
      try {
        const response = await PostService.getEventById(id);
        setEventDetails(response.data);
        setFormData({
          name: response.data.name,
          description: response.data.description,
          place: response.data.place,
          date: response.data.date,
          time: response.data.time,
          maxParticipants: response.data.maxParticipants,
        });
        setLoading(false);
      } catch (error) {
        // Handle error
        setLoading(false);
        console.error("Error fetching event details:", error);
      }
    };

    fetchEventDetails();
  }, [id]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Send formData to update event details
      await PostService.updateEvent(id, formData);
      // Reload or navigate to another page as needed
    } catch (error) {
      // Handle error
      console.error("Error updating event details:", error);
    }
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      {eventDetails && (
        <div className="card border-primary">
          <div className="card-header">
            {eventDetails.name}{" "}
            <Link to={`/editEvent/${id}`} className="btn btn-primary">
              Edit
            </Link>
          </div>
          <div className="card-body">
            <img
              src={`/pictures/${eventDetails.picture}`}
              className="card-img-top"
              alt={eventDetails.name}
              style={{ objectFit: "cover", height: "300px", width: "300px" }}
            />
            <p className="card-text">{eventDetails.description}</p>
            <p className="card-text">
              <small className="text-muted">Место: {eventDetails.place}</small>
            </p>
            {/* Форма для изменения даты и времени */}
            <form onSubmit={handleSubmit}>
              <div className="mb-3">
                <label htmlFor="dateInput" className="form-label">Дата:</label>
                <input type="date" className="form-control" id="dateInput" name="date" value={formData.date} onChange={handleInputChange} />
              </div>
              <div className="mb-3">
                <label htmlFor="timeInput" className="form-label">Время:</label>
                <input type="time" className="form-control" id="timeInput" name="time" value={formData.time} onChange={handleInputChange} />
              </div>
              <button type="submit" className="btn btn-primary">Сохранить</button>
            </form>
            <p className="card-text">
              <small className="text-muted">Всего участников: {eventDetails.maxParticipants}</small>
            </p>
          </div>
        </div>
      )}
    </div>
  );
};

export default EventDetails;
