import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import PostService from "../services/post.service";
import AuthService from "../services/auth.service";
import { Link } from "react-router-dom";

const EventDetails = () => {
  const { id } = useParams();
  const [eventDetails, setEventDetails] = useState(null);
  const [loading, setLoading] = useState(true);
  const [formData, setFormData] = useState({
    name: "",
    description: "",
    place: "",
    date: "",
    maxParticipants: 0,
    placesLeft: 0,
  });
  const user = AuthService.getCurrentUser();

  useEffect(() => {
    const fetchEventDetails = async () => {
      try {
        const response = await PostService.getEventById(id);
        setEventDetails(response.data);
        setFormData({
          name: response.data.name,
          description: response.data.description,
          place: response.data.place,
          date: response.data.date,
          maxParticipants: response.data.maxParticipants,
          placesLeft: response.data.placesLeft,
        });
        setLoading(false);
      } catch (error) {
        // Handle error
        setLoading(false);
        console.error("Error fetching event details:", error);
      }
    };

    fetchEventDetails();
  }, [id]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Send formData to update event details
      await PostService.updateEvent(id, formData);
      // Reload or navigate to another page as needed
    } catch (error) {
      // Handle error
      console.error("Error updating event details:", error);
    }
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      {eventDetails && (
        <div className="card border-primary">
          <div className="card-header">
            {eventDetails.name}{" "}
            <Link to={`/editEvent/${id}`} className="btn btn-primary">
              Edit
            </Link>
          </div>
          <div className="card-body">
          <img
                  src={`/pictures/${eventDetails.picture}`}
                  className="card-img-top"
                  alt={eventDetails.name}
                  style={{ objectFit: "cover", height: "300px", width:"300px" }} // Установите желаемые размеры здесь
                />
            <p className="card-text">{eventDetails.description}</p>
            <p className="card-text">
              <small className="text-muted">Place: {eventDetails.place}</small>
            </p>
            <p className="card-text">
              <small className="text-muted">Date: {eventDetails.date.split("T")[0]}</small>
            </p>
            <p className="card-text">
              <small className="text-muted">Time: {eventDetails.date.split("T")[1]}</small>
            </p>
            <p className="card-text">
              <small className="text-muted">Max participants: {eventDetails.maxParticipants}</small>
            </p>
            <p className="card-text">
              <small className="text-muted">Places left: {eventDetails.placesLeft}</small>
            </p>
          </div>
        </div>
      )}
    </div>
  );
};

export default EventDetails;
