import React, { useState, useEffect } from "react";
import PostService from "../services/post.service";
import AuthService from "../services/auth.service";
import { useNavigate, Link } from "react-router-dom";

const Home = () => {
  const [privatePosts, setPrivatePosts] = useState([]);
  const user = AuthService.getCurrentUser();
 

  const navigate = useNavigate();

  useEffect(() => {
    PostService.getAllEvents().then(
      (response) => {
        setPrivatePosts(response.data);
      },
      async (error) => {
        // Invalid token
        if (error.response == null) {
          //refresh token
          if (user != null) {  }
        }
      }
    );
  }, [navigate]);

  return (
    <div>
      <div className="mt-3">
        <h2>События</h2>
        <div className="row row-cols-1 row-cols-md-3 g-4">
        {privatePosts.map((post) => (
          <div className="col" key={post.id}>
            <div className="card border-primary">
              <div className="card-header">{post.name}</div>
              <img src={post.picture ? `../assets/pictures/${post.picture}` : defaultImage} alt={post.name} className="card-img-top" />
              <div className="card-body">
                <p className="card-text">{post.description}</p>
                <p className="card-text"><small className="text-muted">Место: {post.place}</small></p>
                <p className="card-text"><small className="text-muted">Дата: {new Date(post.date).toLocaleDateString()}</small></p>
              </div>
            </div>
          </div>
        ))}
        </div>
      </div>
    </div>
  );
};

export default Home;
