import React, { useState, useEffect } from "react";
import PostService from "../services/post.service";
import AuthService from "../services/auth.service";
import { useNavigate, Link } from "react-router-dom";

const Events = () => {
  const [privatePosts, setPrivatePosts] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const user = AuthService.getCurrentUser();
 
  const navigate = useNavigate();

  useEffect(() => {
    PostService.getAllEvents().then(
      (response) => {
        setPrivatePosts(response.data);
      },
      async (error) => {
        // Invalid token
        if (error.response == null) {
          //refresh token
          if (user != null) {  }
        }
      }
    );
  }, [navigate, user]);

  // Функция для фильтрации постов по названию
  const filterPosts = (posts, term) => {
    if (!term) {
      return posts;
    }

    return posts.filter((post) =>
      post.name.toLowerCase().includes(term.toLowerCase())
    );
  };

  // Обработчик изменения поискового запроса
  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  // Применение фильтрации к постам
  const filteredPosts = filterPosts(privatePosts, searchTerm);

  return (
    <div>
      <div className="mt-3">
        <h2>Events</h2>
        <div className="input-group mb-3">
          <input
            type="text"
            className="form-control"
            placeholder="Search by name..."
            value={searchTerm}
            onChange={handleSearch}
          />
        </div>
        <div className="row row-cols-1 row-cols-md-3 g-4">
          {filteredPosts.map((post) => (
            <div className="col" key={post.id}>
              <div className="card border-primary">
                <div className="card-header">
                  <Link to={`/eventDetails/${post.id}`}>{post.name}</Link>
                </div>
                <img
                  src={`/pictures/${post.picture}`}
                  className="card-img-top"
                  alt={post.name}
                  style={{ objectFit: "cover", height: "300px" }}
                />
                <div className="card-body">
                  <p className="card-text">{post.description}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Events;
