import { AuthService } from '../services/auth.service'; // Импортируйте AuthService из вашего файла сервисов
import { useNavigate } from 'react-router-dom'; // Импортируйте функцию navigate для перехода по страницам

const navigate = useNavigate();
const handleRefresh = async (user) => {
  if (user != null) {
    await AuthService.loginWithRefreshToken(user.user.refreshToken).then(
      () => {
        window.location.reload();
      },
      () => {
        AuthService.logout();
        navigate("/");
        window.location.reload();
      }
    );
  } else {
    AuthService.logout();
    navigate("/");
    window.location.reload();
  }
};

export default handleRefresh;
