const handleRefresh = async (navigate) => {
    const user = AuthService.getCurrentUser();
  if (user != null) {
    await AuthService.loginWithRefreshToken(user.user.refreshToken).then(
      () => {
        window.location.reload();
      },
      () => {
        console.log("1");
        /*AuthService.logout();
