    // Обработчик выхода из системы
  };

  return (
    <Router>      
      <Navbar isAuthenticated={isAuthenticated} userName={userName}  />
      <Header />
      <Routes>
        <Route path="/login" element={<LoginPage />} />
        <Route path="/register" element={<RegisterPage />} />
        {/* Другие маршруты */}
      </Routes>
    </Router>
  );
}

export default App;
