import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import PostService from "../services/post.service";
import AuthService from "../services/auth.service";

const EventDetails = () => {
  const { id } = useParams();
  const [eventDetails, setEventDetails] = useState(null);
  const [loading, setLoading] = useState(true);
  const user = AuthService.getCurrentUser();

  useEffect(() => {
    const fetchEventDetails = async () => {
      try {
        const response = await PostService.getEventById(id);
        setEventDetails(response.data);
        setLoading(false);
      } catch (error) {
        // Handle error
        setLoading(false);
        console.error("Error fetching event details:", error);
      }
    };

    fetchEventDetails();
  }, [id]);

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      {eventDetails && (
        <div className="card border-primary">
        <div className="card-header">{eventDetails.name}</div>
        <div className="card-body">
          <p className="card-text">{eventDetails.description}</p>
          <p className="card-text"><small className="text-muted">Место: {eventDetails.place}</small></p>
          <p className="card-text"><small className="text-muted">Дата: {new Date(eventDetails.date).toLocaleDateString()}</small></p>
          <p className="card-text"><small className="text-muted">Всего участников: {eventDetails.maxParticipants}</small></p>
        </div>
      </div>
      )}
    </div>
  );
};

export default EventDetails;
