import React, { useState, useEffect } from "react";
import PostService from "../services/post.service";
import AuthService from "../services/auth.service";
import { useNavigate, Link } from "react-router-dom";

const Events = () => {
  const [privatePosts, setPrivatePosts] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [currentPage, setCurrentPage] = useState(1);
  const [postsPerPage] = useState(3); // Количество элементов на странице
  const user = AuthService.getCurrentUser();
 
  const navigate = useNavigate();

  useEffect(() => {
    const fetchPosts = async () => {
      const response = await PostService.getAllEvents();
      setPrivatePosts(response.data);
    };

    fetchPosts();
  }, [navigate, user]);

  // Функция для фильтрации постов по названию
  const filterPosts = (posts, term) => {
    if (!term) {
      return posts;
    }

    return posts.filter((post) =>
      post.name.toLowerCase().includes(term.toLowerCase())
    );
  };

  // Применение фильтрации и пагинации к постам
  const indexOfLastPost = currentPage * postsPerPage;
  const indexOfFirstPost = indexOfLastPost - postsPerPage;
  const currentPosts = filterPosts(privatePosts, searchTerm)
    .slice(indexOfFirstPost, indexOfLastPost);

  // Обработчик изменения поискового запроса
  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  // Обработчик изменения текущей страницы
  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  return (
    <div>
      <div className="mt-3">
        <h2>Events</h2>
        <div className="input-group mb-3">
          <input
            type="text"
            className="form-control"
            placeholder="Search by name..."
            value={searchTerm}
            onChange={handleSearch}
          />
        </div>
        <div className="row row-cols-1 row-cols-md-3 g-4">
          {currentPosts.map((post) => (
            <div className="col" key={post.id}>
              <div className="card border-primary">
                <div className="card-header">
                  <Link to={`/eventDetails/${post.id}`}>{post.name}</Link>
                </div>
                <img
                  src={`/pictures/${post.picture}`}
                  className="card-img-top"
                  alt={post.name}
                  style={{ objectFit: "cover", height: "300px" }}
                />
                <div className="card-body">
                  <p className="card-text">{post.description}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
        {/* Пагинация */}
        <ul className="pagination justify-content-center">
          {Array.from({ length: Math.ceil(filterPosts(privatePosts, searchTerm).length / postsPerPage) }, (_, i) => (
            <li className={`page-item ${currentPage === i + 1 ? 'active' : ''}`} key={i}>
              <button className="page-link" onClick={() => paginate(i + 1)}>{i + 1}</button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Events;
