import React, { useState, useEffect } from "react";
import PostService from "../services/post.service";
import AuthService from "../services/auth.service";
import { useNavigate, Link } from "react-router-dom";

const Events = () => {
  const [privatePosts, setPrivatePosts] = useState([]);
  const [filteredPosts, setFilteredPosts] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [currentPage, setCurrentPage] = useState(1);
  const [postsPerPage] = useState(3); // Количество событий на одной странице
  const user = AuthService.getCurrentUser();
 
  const navigate = useNavigate();

  useEffect(() => {
    PostService.getAllEvents().then(
      (response) => {
        setPrivatePosts(response.data);
        setFilteredPosts(response.data); // Установка начального значения filteredPosts
      },
      async (error) => {
        // Invalid token
        if (error.response == null) {
          //refresh token
          if (user != null) {  }
        }
      }
    );
  }, [navigate, user]);

  // Фильтрация элементов по поисковому запросу
  const filterPosts = (term) => {
    const filtered = privatePosts.filter((post) =>
      post.name.toLowerCase().includes(term.toLowerCase())
    );
    setFilteredPosts(filtered);
    setCurrentPage(1); // Сброс текущей страницы при изменении фильтрации
  };

  // Обработчик изменения поискового запроса
  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
    filterPosts(e.target.value);
  };

  // Получение текущих постов на странице
  const indexOfLastPost = currentPage * postsPerPage;
  const indexOfFirstPost = indexOfLastPost - postsPerPage;
  const currentPosts = filteredPosts.slice(indexOfFirstPost, indexOfLastPost);

  // Функция для изменения текущей страницы
  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  return (
    <div>
      <div className="mt-3">
        <h2>Events</h2>
        <div className="input-group mb-3">
          <input
            type="text"
            className="form-control"
            placeholder="Search by name..."
            value={searchTerm}
            onChange={handleSearch}
          />
        </div>
        <div className="row row-cols-1 row-cols-md-3 g-4">
          {currentPosts.map((post) => (
            <div className="col" key={post.id}>
              <div className="card border-primary">
                <div className="card-header">
                  <Link to={`/eventDetails/${post.id}`}>{post.name}</Link>
                </div>
                <img
                  src={`/pictures/${post.picture}`}
                  className="card-img-top"
                  alt={post.name}
                  style={{ objectFit: "cover", height: "300px" }} // Установите желаемые размеры здесь
                />
                <div className="card-body">
                  <p className="card-text">{post.description}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
        {/* Элементы управления пагинацией */}
        <nav>
          <ul className="pagination">
            {Array.from({ length: Math.ceil(filteredPosts.length / postsPerPage) }).map((_, index) => (
              <li key={index} className={`page-item ${currentPage === index + 1 ? "active" : ""}`}>
                <button className="page-link" onClick={() => paginate(index + 1)}>
                  {index + 1}
                </button>
              </li>
            ))}
          </ul>
        </nav>
      </div>
    </div>
  );
};

export default Events;
