import React, { useState, useEffect } from "react";
import PostService from "../services/post.service";
import AuthService from "../services/auth.service";
import { useNavigate, Link } from "react-router-dom";

const Home = () => {
  const [privatePosts, setPrivatePosts] = useState([]);
  const user = AuthService.getCurrentUser();
  const logOut = () => {
    AuthService.logout();
  };

  const navigate = useNavigate();

  useEffect(() => {
    PostService.getAllEvents().then(
      (response) => {
        setPrivatePosts(response.data);
      },
      async (error) => {
        // Invalid token
        if (error.response == null) {
          //refresh token
          if (user != null) {
            
          } 
        }
      }
    );
  }, [user, navigate]);

  return (
    <div>
      <div className="d-grid gap-2 mt-3">
        <Link to="/" onClick={logOut}>
          <button type="submit" className="btn btn-primary">
            Logout
          </button>
        </Link>
      </div>

      <div className="mt-3">
        <h2>События</h2>
        <div className="row row-cols-1 row-cols-md-3 g-4">
          {privatePosts.map((post) => (
            <div className="col" key={post.id}>
              <div className="card">
                <img src={post.picture} className="card-img-top" alt={post.name} />
                <div className="card-body">
                  <h5 className="card-title">{post.name}</h5>
                  <p className="card-text">{post.description}</p>
                  <p className="card-text"><small className="text-muted">Место: {post.place}</small></p>
                  <p className="card-text"><small className="text-muted">Дата: {new Date(post.date).toLocaleDateString()}</small></p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Home;
